import React, { useEffect, useState } from 'react';
import { initializeApp } from 'firebase/app';
import { getFirestore, collection, getDocs, doc, addDoc } from 'firebase/firestore/lite';

const firebaseConfig = {
  // Your Firebase config here
};

const app = initializeApp(firebaseConfig);
const db = getFirestore(app);

export default function TakeAttendance() {
  const [employeeData, setEmployeeData] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const itemsCol = collection(db, 'employeeDetails');
      const itemSnapshot = await getDocs(itemsCol);
      const data = itemSnapshot.docs.map((doc) => ({ id: doc.id, ...doc.data() }));
      setEmployeeData(data);
    };

    fetchData();
  }, []);

  const [attendanceData, setAttendanceData] = useState([]);

  const handleAttendanceChange = (e, id) => {
    if (e.target.checked) {
      // Mark employee as absent
      setAttendanceData((prevAttendance) => [...prevAttendance, id]);
    } else {
      // Mark employee as present
      setAttendanceData((prevAttendance) => prevAttendance.filter((absentId) => absentId !== id));
    }
  };

  const handleSaveAttendance = async () => {
    try {
      // Create a new collection "employeeAttendance" in the database
      const attendanceCol = collection(db, 'employeeAttendance');

      // Prepare the data for the new collection
      const attendanceData = {
        date: new Date().toISOString(),
        absentEmployees: attendanceData,
      };

      // Add the data to the "employeeAttendance" collection
      await addDoc(attendanceCol, attendanceData);

      // Reset the attendanceData state after saving
      setAttendanceData([]);

      alert('Attendance saved successfully!');
    } catch (error) {
      console.error('Error saving attendance:', error);
      alert('Error saving attendance. Please try again.');
    }
  };

  return (
    <div className="dashboard-content-one" id="cont-1">
      {/* ... Rest of the code ... */}
      <table className="table display data-table text-nowrap">
        <thead>
          <tr>
            <th>S.No</th>
            <th>Full Name</th>
            <th>Employee ID</th>
            <th>Absent</th>
          </tr>
        </thead>
        <tbody>
          {employeeData.map((employee, index) => (
            <tr key={employee.id}>
              <td>{index + 1}</td>
              <td>{employee.fullName}</td>
              <td>{employee.employeeId}</td>
              <td>
                <input
                  type="checkbox"
                  checked={attendanceData.includes(employee.id)}
                  onChange={(e) => handleAttendanceChange(e, employee.id)}
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <button onClick={handleSaveAttendance}>Save Attendance</button>
    </div>
  );
}
