import React, { useState } from 'react';

export default function AddEmployee() {
  const [formData, setFormData] = useState({
    fullName: '',
    employeeId: '',
    gender: '',
    dateOfBirth: '',
    contactInfo: '',
    maritalStatus: '',
    nationalIdNumber: '',
    passportInfo: '',
    emergencyContact: '',
    bankAccountDetails: '',
    taxAndSalaryInfo: '',
    residencyStatus: '',
    employmentStartDate: '',
    employmentStatus: '',
    jobTitle: '',
    department: '',
    reportingManager: '',
    compensationDetails: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({ ...prevData, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (Object.values(formData).every((value) => value.trim() !== '')) {
      console.log('Submitted Data:');
      console.log(formData);
      // You can perform further actions here, such as saving the data to a backend server.
      // For demonstration purposes, we're just logging the data to the console.
    } else {
      console.log('Please fill in all the fields.');
    }
  };

  return (
    <div className="dashboard-content-one" id="cont">
      <div className="breadcrumbs-area">
        <h3>Add New Employee</h3>
      </div>
      <div className="card height-auto">
        <div className="card-body">
          <div className="heading-layout1">
            <div className="dropdown">
              <div className="dropdown-menu dropdown-menu-right">
                <a className="dropdown-item" href="#">
                  <i className="fas fa-times text-orange-red" />
                  Close
                </a>
                <a className="dropdown-item" href="#">
                  <i className="fas fa-cogs text-dark-pastel-green" />
                  Edit
                </a>
                <a className="dropdown-item" href="#">
                  <i className="fas fa-redo-alt text-orange-peel" />
                  Refresh
                </a>
              </div>
            </div>
          </div>
          <form className="new-added-form" onSubmit={handleSubmit}>
            <div className="row">
              {/* Your input fields for each data field */}
              {/* ... */}
              <div className="col-12 form-group mg-t-8">
                <button
                  type="submit"
                  className="btn-fill-lg btn-gradient-yellow btn-hover-bluedark"
                  disabled={!Object.values(formData).every((value) => value.trim() !== '')}
                >
                  Save
                </button>
                <button
                  type="reset"
                  className="btn-fill-lg bg-blue-dark btn-hover-yellow"
                >
                  Reset
                </button>
              </div>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}
